function filtrarRepositorio(){const input=$("#filtraRepositorio");input.on("input",function(){const tr=$("tr");input.val();for(let i=0;i<tr.length;i++){let linhas=tr[i],nomeTd=linhas.querySelector(".nome").innerText,expressao=new RegExp(this.value,"i");linhas.classList.add("d-none"),expressao.test(nomeTd)&&linhas.classList.remove("d-none")}})}