async function salvandoSessionStorage(user){let repositorio,starred;await fetch(`https://api.github.com/users/${user.login}/repos`).then(resolve=>resolve.json()).then(json=>{repositorio=json}),await fetch(`https://api.github.com/users/${user.login}/starred`).then(resolve=>resolve.json()).then(json=>{starred=json;const usuario={avatar_url:user.avatar_url,login:user.login,followers:user.followers,bio:user.bio,repositorio:repositorio,starred:starred},usuarioLS=JSON.stringify(usuario);sessionStorage.setItem(usuario.login.toLowerCase(),usuarioLS)})}